script:
frappe.ui.form.on("Asset", {
	validate: function(frm){
     		if (frm.doc.__islocal) {
        		frm.doc.asset_name = frm.doc.item_code
		}
	} 
});

frappe.ui.form.on("Item", {
	validate: function(frm){
     		if (frm.doc.asset_location && frm.doc.asset_category_code && frm.doc.__islocal) {
        		frappe.call({
	            	method: 'client.apiclient.asset_series',
	           	 args: {
	                	'asset_category_code': frm.doc.asset_category_code,
	                	'asset_location': frm.doc.asset_location
	            	},
	            	callback: function(r) {
				if(r.message){
					console.log(r.message);
					frm.doc.item_code = r.message;
					//frm.doc.name = r.message;
	            			//frm.set_value("item_code", r.message);
					//frm.set_value("name", r.message);
				}
	            	}
			});
		}
	} 
});

frappe.ui.form.on("Employee", {
 attendance_hours: function(frm,cdt,cdn) {
        frappe.call({
    method: 'frappe.client.get_value',
    args: {
        'doctype': 'Attendance Hours',
        'filters': {'name': frm.doc.attendance_hours},
        'fieldname':['weekly_holidays','branch']
    },
    callback: function(r) {
	frappe.model.set_value(cdt, cdn, "holiday_list", r.message.weekly_holidays);
	frappe.model.set_value(cdt, cdn, "branch", r.message.branch);
    }
});

}
});
