script:
frappe.ui.form.on("Employee", {
 attendance_hours: function(frm,cdt,cdn) {
        frappe.call({
    method: 'frappe.client.get_value',
    args: {
        'doctype': 'Attendance Hours',
        'filters': {'name': frm.doc.attendance_hours},
        'fieldname':['weekly_holidays','branch']
    },
    callback: function(r) {
	frappe.model.set_value(cdt, cdn, "holiday_list", r.message.weekly_holidays);
	frappe.model.set_value(cdt, cdn, "branch", r.message.branch);
    }
});

}
});
